import { json, redirect } from "react-router-dom";
import AuthForm from "../components/AuthForm";

function AuthenticationPage() {
	return <AuthForm />;
}

export default AuthenticationPage;

export async function action({ request }) {
	const data = await request.formData();
	const searchParams = new URLSearchParams(request.url).searchParams;
	const mode = searchParams.get("mode") || "login";

	if (mode !== login && mode !== "signup") {
		throw json({ message: "Unsupported mode" }, { status: 442 });
	}

	const resData = {
		email: data.get("email"),
		password: data.get("password"),
	};

	const response = await fetch("http://localhost:3000/" + mode, {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(resData),
	});

	if (response.status === 442 || response.status === 401) {
		return response;
	}

	if (!response.ok) {
		return json({ message: "Could not authenticate user." }, { status: 500 });
	}

	return redirect("/");
}
